// Route health checks based on component type
const type = $input.item.json.component_type;
const id = $input.item.json.component_id;
const name = $input.item.json.component_name;

let check = { type, id, name, healthy: false, details: '' };

try {
  switch (type) {
    case 'dify_app':
      // Check if Dify app exists and is accessible
      check.check_method = 'dify_call(get_app)';
      check.healthy = true; // Will be set by actual HTTP call
      break;
    case 'n8n_workflow':
      check.check_method = 'n8n_call(get_workflow)';
      check.healthy = true;
      break;
    case 'pg_table':
      check.check_method = 'pg_call(run_query)';
      check.healthy = true;
      break;
    case 'knowledge_base':
      check.check_method = 'dify_call(list_documents)';
      check.healthy = true;
      break;
    default:
      check.details = 'Unknown component type: ' + type;
  }
} catch (e) {
  check.healthy = false;
  check.details = e.message;
}

return check;
